mapper.select:
SELECT
      l.date,
      sum(l.number) AS number,
      c. NAME
  FROM
      yw_community_login_statistics l
  LEFT JOIN yw_community c ON c.id = l.community_id
  WHERE c.city_code = #{cityCode}
          and l.date >= #{startTime}
          and l.date<=#{endTime}

              and c.company_id =#{companyId}

  GROUP BY
      l.community_id

SELECT l.date, sum(l.number) AS number, c. NAME FROM yw_community_login_statistics l LEFT JOIN yw_community c ON c.id = l.community_id
1=1
and c.city_code = #{cityCode}
and l.date >= #{startTime}
and l.date<=#{endTime}
and c.company_id =#{companyId}
GROUP BY l.community_id ORDER BY number DESC
