mapper.select:
SELECT * FROM yw_user_extend ue LEFT JOIN  sys_user u ON ue.user_id = u.user_id

    1=1

       AND  u.username = #{userPhone}


        AND  ue.company_id = #{companyId}


        AND  ue.community_id = #{communityId}


        AND  ue.user_id = #{userId}

SELECT * FROM yw_user_extend

    1=1

        AND  user_id = #{userId}


        AND  company_id = #{companyId}


        AND  community_id = #{communityId}


        AND  check = #{check}


        AND  status = #{status}


        AND  company_role_type = #{companyRoleType}

SELECT
  e.*,c.name as communityName
FROM
    yw_user_extend e
LEFT JOIN yw_community c ON e.community_id = c.id

    1=1

        and e.community_id = #{communityId}


        and c.company_id = #{companyId}


        and e.user_id = #{userId}


        and c.name = #{communityName}


        and e.company_role_type = #{companyRoleType}


ORDER BY e.create_time DESC
