mapper.select:
SELECT A.* FROM yw_community A LEFT JOIN prov_city_data B ON A.city_id = b.id

   enable > 0

       AND A.name like concat('%',#{name},'%')


       AND A.city_id = #{cityId}


       AND B.city_name = #{cityName}


       AND B.city_code = #{cityCode}


       AND A.company_id = #{companyId}

SELECT A.*,B.CITY_NAME AS cityName FROM yw_community A LEFT JOIN prov_city_data B ON A.city_id = b.id

    enable > 0

        AND A.name like  concat('%',#{name},'%')


        AND A.city_code = #{cityCode}


        AND A.company_id = #{companyId}


        AND B.city_name = #{cityName}

mapper.update:
UPDATE yw_community set enable=0, delete_time =#{deleteTime}
where id in

    #{id}
