mapper.select:
SELECT r.id, r.user_id, r.create_time, r.repairs_time, r.location, r.detail, r.repairs_id, r.repairs_type, r.`status`, u.hand_img_id AS imgId, u.nick_name as nickName FROM yw_repairs_info r LEFT JOIN sys_user u ON r.user_id = u.user_id
1=1
and user_id =#{userId}
and community_id =#{communityId}
and repairs_type =#{repairsType}
and status =#{status}
and repairs_id =#{repairsId}
ORDER BY r.create_time DESC

SELECT r.id, r.user_id, r.community_id, r.create_time, r.repairs_time, r.location, r.detail, r.repairs_id, r.repairs_type, r.service_date, r.`status`, u.hand_img_id AS imgId, u.real_name as userRealName, u.username as userPhone, u.nick_name as nickName FROM yw_repairs_info r LEFT JOIN sys_user u ON r.user_id = u.user_id
1=1
and r.user_id =#{userId}
and r.community_id =#{communityId}
and r.repairs_type =#{repairsType}
and r.status >= 2
and r.status = #{status}
and r.community_id in
#{communityId}
and r.create_time >= #{startTime}
and r.create_time <= #{endTime}
and r.repairs_id =#{repairsId}
ORDER BY r.create_time DESC

SELECT i.id, i.user_id, i.community_id, i.create_time, i.location, i.detail, r.user_id as repairsId, i.repairs_type, i.service_date, i.repairs_time, i.`status`, s.hand_img_id AS imgId, s.real_name AS userRealName, s.username AS userPhone FROM yw_repairs_info i LEFT JOIN yw_user_repairs r ON i.id = r.repairs_id LEFT JOIN sys_user s ON s.user_id = i.user_id
1=1
and i.repairs_type =#{repairsType}
and i.status < 2
and i.status =#{status}
and i.community_id in
#{communityId}
and r.user_id =#{repairsId}
ORDER BY i.create_time DESC
